}
int main() {
    // initialize map with state capitals
    map<string, string> states_map = {
        {"Alabama", "Montgomery"},
        {"Alaska", "Juneau"},
        {"Arizona", "Phoenix"},
        // more state capitals here
    };
    // initialize vector to store selected capitals
    vector<string> selected_capitals;
    // set seed for random number generator
    srand(time(NULL));
    // loop until three unique and non-adjacent capitals are selected
    while (selected_capitals.size() < 3) {
        // generate random index to select a state capital from the map
        int random_index = rand() % states_map.size();
        auto iter = states_map.begin();
        advance(iter, random_index);
        string state = iter->first;
        string capital = iter->second;
        // check if the selected state capital is already in the vector
        if (find(selected_capitals.begin(), selected_capitals.end(), capital) != selected_capitals.end()) {
            continue;
        }
        // check if the selected state capital is adjacent to any already selected state capitals
        bool adjacent = false;
        for (auto s : selected_capitals) {
            if (isAdjacent(s, capital)) {
                adjacent = true;
                break;
            }
        }
        if (adjacent) {
            continue;
        }
        // add the selected state capital to the vector
        selected_capitals.push_back(capital);
    }
    // print the selected state capitals
    for (auto c : selected_capitals) {
        cout << c << endl;
    }
    return 0;
}

