        // Draw icon
        g.setColor(Color.BLUE);
        g.fillOval(width/2 + 30, height/2 - 60, 100, 100);
        // Save image
        try {
            ImageIO.write(image, "png", new File("ace_logo.png"));
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
