function calculateEnergyUsage(devices, startTime, endTime) {
  let totalEnergy = 0;
  let xmlString = "<energyUsage>";
  for (let i = 0; i < devices.length; i++) {
    if (devices[i].timePeriod >= startTime && devices[i].timePeriod <= endTime) {
      totalEnergy += devices[i].energyUsage;
      xmlString += "<device>";
      xmlString += "<type>" + devices[i].deviceType + "</type>";
      xmlString += "<energyUsage>" + devices[i].energyUsage + "</energyUsage>";
      xmlString += "</device>";
    }
  }
  xmlString += "<total>" + totalEnergy + "</total>";
  xmlString += "</energyUsage>";
  return xmlString;
}
