function addTwo(x, y) {
  if (isNaN(x) || isNaN(y)) {
    return NaN;
  } else if (x < 0 && y < 0) {
    return -1 * (Math.abs(x) + Math.abs(y));
  } else {
    return (Math.round((x + y) * 100) / 100);
  }
}
