```
class Book {
    var title: String
    var author: String
    var pagesRead: Int
    var totalPages: Int

    init(title: String, author: String, pagesRead: Int, totalPages: Int) {
        self.title = title
        self.author = author
        self.pagesRead = pagesRead
        self.totalPages = totalPages
    }
}
var books: [Book] = []
