    @IBOutlet weak var titleLabel: UILabel!
    @IBOutlet weak var authorLabel: UILabel!
    @IBOutlet weak var progressLabel: UILabel!
    @IBOutlet weak var pagesReadTextField: UITextField!

    override func viewDidLoad() {
        super.viewDidLoad()
        titleLabel.text = book.title
        authorLabel.text = book.author
        progressLabel.text = "\(book.pagesRead)/\(book.totalPages) pages read"
    }

    @IBAction func updatePagesRead(_ sender: Any) {
        if let pagesRead = Int(pagesReadTextField.text!) {
            book.pagesRead = pagesRead
            progressLabel.text = "\(book.pagesRead)/\(book.totalPages) pages read"
            pagesReadTextField.text = ""
        }
    }
}
