[C++ code]
#include <iostream>
#include <string>
#include <cstdlib>
#include <ctime>
using namespace std;
string adjs[] = {"subterranean", "hidden", "underground", "buried", "secret"};
string nouns[] = {"tributary", "river", "stream", "brook", "creek"};
string verbs[] = {"flows", "rushes", "trickles", "meanders", "ripples"};
string generateLine(int syllables, string arr[]) {
    string line = "";
    int count = 0;
    while (count < syllables) {
        int index = rand() % 5;
        string word = arr[index];
        int sylCount = 0;
        if (word.length() == 5) {
            sylCount = 1;
        }
        else if (word.length() == 7) {
            sylCount = 2;
        }
        else if (word.length() == 9) {
            sylCount = 3;
        }
        if (count + sylCount <= syllables) {
            line += word;
            line += " ";
            count += sylCount;
        }
    }
    return line;
}
int main() {
    srand(time(NULL));
    string line1 = generateLine(5, adjs);
    string line2 = generateLine(7, nouns);
    string line3 = generateLine(5, verbs);
    cout << line1 << endl << line2 << endl << line3 << endl;
    return 0;
} 
